// Function to get SEO data for a specific page
export const getSEOData = (pathname) => {
  const path = pathname.replace("/", "") || "home";

  // Check if it's a product category page
  if (
    path.includes("products/") ||
    path.includes("analgesic") ||
    path.includes("antibiotic") ||
    path.includes("cardiovascular") ||
    path.includes("ayurvedic") ||
    path.includes("dry-syrups") ||
    path.includes("surgical")
  ) {
    const category = path.split("/").pop() || path;
    return {
      ...seoConfig.default,
      ...(seoConfig.productCategories[category] || seoConfig.pages.products),
    };
  }

  // Return page-specific SEO data or default
  return {
    ...seoConfig.default,
    ...(seoConfig.pages[path] || seoConfig.pages.home),
  };
};
